// Clicktool.js
import React from "react";
import './clicktool.css'
import Geometry from './SVG/ClicktoolLogoGeometry.js'
import Logo from '../../../../../IMG/ClicktoolLogoHorizontal.js'

class Clicktool extends React.Component {
    // state = {
    //   displaymobileNav: false,
    //   menuText: "Menu"
    // };

    render() {
      return (
          <div className="clicktool">

            {/* top section */}
            <section className="relative">

                <div className="geometry absolute flex-center whole md-p30">
                    <Geometry />
                </div>

                <div className="sticky-top relative pl30 pr30 pb30">
                    <h6>Project:</h6>
                    <h5>Clicktool</h5>
                    <p className="whole md-half lg-third xl-fourth">
                        Clicktool is a marketing analytics app designed for digital marketers. Using our app, users can track clicks, phone calls, conversions, and many other types of metrics that help marketers make better decisions about where to spend theier money.                        
                    </p>
                </div>

            </section>

            {/* roles section */}
            <section className="roles">
                <div className="sticky-top pl30 pr30 pb30">
                    <h6 className="">MY ROLES FOR THIS PROJECT:</h6>
                    <div className="one-column md-four-columns gap10">
                        <div>
                            <h5>PRODUCT DESIGNER</h5>
                            <p>Understand business objectives and design solutions based on direction from the CEO and user research</p>
                        </div>

                        <div>
                            <h5>CREATIVE DIRECTOR</h5>
                            <p>Lead the creation of this from top to bottom while collaborating with other designers, developers, and copywriters.</p>
                        </div>

                        <div>
                            <h5>UI/UX DESIGNER</h5>
                            <p>Create wireframes, prototypes, color schemes, design systems, brand guidelines, and custom CSS framework.</p>
                        </div>
                        <div>
                            <h5>FRONT-END DEVELOPER</h5>
                            <p>In a React.js environment, use various technology to </p>
                        </div>
                    </div>


                    <p className="whole md-half lg-third xl-fourth hg-fifth">
                        Clicktool is a marketing analytics app designed for digital marketers. Using our app, users can track clicks, phone calls, conversions, and many other types of metrics that help marketers make better decisions about where to spend theier money.                        
                    </p>
                </div>
            </section>
          </div>
      );
    }
  }

  export default Clicktool;
